sp_widget.client_script:
api.controller=function($rootScope, $scope) {
  /* widget controller */
  var c = this;

	c.noteIndex = null;

	c.selectedItem = function(idx) {
		c.noteIndex = idx;
		var id = c.data.notes[idx].sys_id;
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit("selectedNote", id);	
	}

	$rootScope.$on("refreshList", function(event, args) {
		console.log("Listener caught refresh event: " + JSON.stringify(args));
		c.data.notes[c.noteIndex].title = args.title;
		c.data.notes[c.noteIndex].note = args.note;
	})
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var notes = [];
	var gr = new GlideRecord("x_snc_createnotes_note");
	gr.addQuery("user", gs.getUserID());
	gr.orderByDesc("sys_created_on");
	gr.query();

	while (gr.hasNext()) {
		gr.next();
		var n = {}
		$sp.getRecordDisplayValues(n, gr, "number,title,sys_id");
		n.note = gr.getValue("note").substr(0,20);
		notes.push(n);
	}
	data.notes = notes;
})();
